times = 0
while times < 5:
    #Get screenshot
    screen = pag.screenshot()
    screen = np.array(screen)
    screen = cv2.cvtColor(screen, cv2.COLOR_BGR2RGB)

    res = cv2.matchTemplate(screen, button, cv2.TM_CCOEFF_NORMED)
    threshold = 0.98
    loc = np.where(res >= threshold)
    cords = []
    for pt in zip(*loc[::-1]):
        cords.append((int(pt[0]+button.shape[1]/2), int(pt[1]+button.shape[0]/2)))

    for cord in cords:
        pag.click(cord[0], cord[1])
        sleep(0.05)
    
    if len(cords) == 0:
        times+=1
    else:
        times = 0
    pag.press("pagedown")
    pag.moveTo(0,0)
    print(times)
    sleep(0.5)
    